namespace Mageplaza\MassProductActions\Model\Config\Source;

use Magento\Framework\Option\ArrayInterface;

/**
 * Class Direction
 * @package Mageplaza\MassProductActions\Model\Config\Source
 */
class Direction implements ArrayInterface
{
    const ONE_WAY    = 1;
    const MUTUAL_WAY = 2;

    /**
     * @return array
     */
    public function toOptionArray()
    {
        $options = [];
        foreach ($this->toArray() as $value => $label) {
            $options[] = compact('value', 'label');
        }

        return $options;
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return [
            self::ONE_WAY    => __('One-way relation'),
            self::MUTUAL_WAY => __('Mutual-way relation')
        ];
    }
}
